package javaPrograms;

public class Loops2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// print numbers 1 to 10 print value even number;
		
		int i =1;
	while(i<=10)
	{
		if(i % 2 ==0)
		System.out.println(i);
		i++;
	}
	
	
	int j =1;
	
	while(j<=10)
	{
		if(j %2 ==1)
			System.out.println(j);
		j++;
	}
	
// do while loop
	
	int x= 11;
	do {
		System.out.println(x);
		x++;
	}
	while(x<=20);
	
	
	
	
	
	
	
	
	
	
	
	
	
	

	}

}
