package javaPrograms;

public class LoopsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// synatx:
		// for(start value;condition;incrementorDecrement)
		
		// print numbers 1 to 10
		// i++ : increment its value by 1
		// i-- : decrement its value by 1
		
		for(int i=1;i<=10;i++)
		{
		System.out.println(i);
		}

		
		System.out.println("We are out of the loop, the value of I must be now greater than 10");
	}

}
